"use strict";

// добавление нулей 
function zeroFirstFormat(value) {
  if (value < 10) {
    value = '0' + value;
  }

  return value;
} // получение текущей даты и времени


function dateTime() {
  var currentDatetime = new Date();
  var day = zeroFirstFormat(currentDatetime.getDate());
  var month = zeroFirstFormat(currentDatetime.getMonth() + 1);
  var year = currentDatetime.getFullYear();
  var hours = zeroFirstFormat(currentDatetime.getHours());
  var minutes = zeroFirstFormat(currentDatetime.getMinutes());
  var seconds = zeroFirstFormat(currentDatetime.getSeconds());
  return "".concat(day, ".").concat(month, ".").concat(year, " ").concat(hours, ":").concat(minutes, ":").concat(seconds);
} // вывод даты


setInterval(function () {
  document.getElementById('js-clock').innerHTML = dateTime();
}, 1000); //валидация формы

var pass = document.getElementById("formPass");
pass.addEventListener("input", function (e) {
  e.preventDefault();

  if (pass.validity.patternMismatch) {
    pass.setCustomValidity("Пароль должен содержать не менее 6 символов, включая заглавные буквы, цифры и спецсимволы");
  } else {
    pass.setCustomValidity("");
  }
}); //проверка на ie

isIE();

function isIE() {
  var ua = window.navigator.userAgent; //Check the userAgent property of the window.navigator object

  var msie = ua.indexOf('MSIE '); // IE 10 or older

  var trident = ua.indexOf('Trident/'); //IE 11

  if (msie > 0 || trident > 0) {
    return alert('ваш браузер устарел');
  } else return;
}